# print(ss.minimizedMaximum(n, quantities))


import heapq

class Solution:
    def maximalPathQuality(self, values: List[int], edges: List[List[int]], maxTime: int) -> int:
        n = len(values)
        dis = [-1] * n
        E = {i: [] for i in range(n)}
        for a, b, t in edges:
            E[a].append((b, t))
            E[b].append((a, t))

        dis[0] = 0
        maxDis = (maxTime + 1) // 2

        heap = [(0, 0)]
        visited = [0] * n

        heapq.heapify(heap)

        while heap:
            cur_dis, cur_node = heapq.heappop(heap)

            if cur_dis > maxDis:
                break

            visited[cur_node] = 1
            dis[cur_node] = cur_dis

            for neighbor, d in E[cur_node]:
                if visited[neighbor] == 0:
                    heapq.heappush(heap, (cur_dis + d, neighbor))

        ans = 0

        def dfs(cur_node, cur_path, rest_time, cur_ans):
            nonlocal ans

            if rest_time < 0:
                return

            if cur_node not in cur_path:
                cur_ans += values[cur_node]
                cur_path += (cur_node, )
            if cur_node == 0:
                ans = max(cur_ans, ans)

            for neighbor, d in E[cur_node]:
                if dis[neighbor] == -1 or dis[neighbor] > rest_time:
                    continue
                dfs(neighbor, cur_path, rest_time - d, cur_ans)

        dfs(0, (), maxTime, 0)
        return ans

s = Solution()
values = [0,32,10,43]
edges = [[0,1,10],[1,2,15],[0,3,10]]
maxTime = 49

values = [5,10,15,20]
edges = [[0,1,10],[1,2,10],[0,3,10]]
maxTime = 30

values = [1,2,3,4]
edges = [[0,1,10],[1,2,11],[2,3,12],[1,3,13]]
maxTime = 50

values = [1,1,2]
edges = [[1,2,10]]
maxTime = 10
print(s.maximalPathQuality(values, edges, maxTime))


























































































